INSERT INTO `co_engineering_manual` VALUES (2,2,'灯制一代项目书','<p><input style=\"margin-right: 10px\" type=\"image\" height=\"173\" width=\"174\" src=\"/easyphp/userfiles/image/bear.JPG\" align=\"left\" /></p>\r\n<h3>Bear Bibeault Bear</h3>\r\n<p>大學之道，在明明德，在清明，在止於至善.</p>\r\n<p>BEAR BIBEAULT has been writing software for over<br />\r\nthree decades, starting with a Tic-Tac-Toe program<br />\r\nwritten on a Control Data Cyber supercomputer via<br />\r\na 100-baud teletype. Because he has two degrees in<br />\r\nElectrical Engineering, Bear should be designing<br />\r\nantennas or something; but, since his first real job<br />\r\nwith Digital Equipment Corporation, he has always<br />\r\nbeen much more fascinated with programming.<br />\r\nBear has also served stints with companies such<br />\r\nas Lightbridge Inc., BMC Software, Dragon Systems, and even served in the<br />\r\nU. S. Military teaching infantry soldiers how to blow up tanks. (Care to guess<br />\r\nwhich job was the most fun?) Bear is currently a Software Architect and Technical<br />\r\nManager for a company that builds and maintains a large financial web<br />\r\napplication used by the accountants that many of the Fortune 500 companies<br />\r\nkeep in their dungeons.<br />\r\nIn addition to his day job, Bear also writes books (duh!), runs a small business<br />\r\nthat creates web applications and offers other media services (but not wedding<br />\r\nvideography, never wedding videography), and helps to moderate<br />\r\nJavaRanch.com as a &ldquo;sheriff.&rdquo; When not planted in front of a computer, Bear<br />\r\nlikes to cook big food (which accounts for his jeans size), dabble in photography<br />\r\nand video editing, ride his Yamaha V-Star, and wear tropical print shirts.</p>\r\n<p>&nbsp;&nbsp;&nbsp; He works and resides in Austin, Texas, a city he dearly loves except for the<br />\r\ncompletely insane drivers.</p>\r\n<p>&quot;</p>','700','CO@TECHWISE.COM.HK',1229328739546,'CO@TECHWISE.COM.HK',1235804099933,'0'),(3,5,'灯制三代工程指南','<p><input style=\"margin-right: 10px\" type=\"image\" height=\"150\" width=\"200\" src=\"/easyphp/userfiles/image/Winter.jpg\" align=\"left\" /></p>\r\n<h3>Type the title here</h3>\r\n<p>Winter forest is a beatiful senery.</p>\r\n<p>Welcome to winter forest!</p>\r\n<p>Edited by acaleph8@yahoo.com.cn</p>\r\n<p>&nbsp;</p>\r\n<p>AAAAA具工</p>','700','CO@TECHWISE.COM.HK',1229386597771,'CO@TECHWISE.COM.HK',1247125529562,'0'),(4,NULL,'人體感應開關','<p><img style=\"width: 157px; margin-right: 10px; height: 159px\" alt=\"\" align=\"left\" src=\"/easyphp/userfiles/Sunset(1).jpg\" /></p>\r\n<h3>Type the title here</h3>\r\n<p>Type the text here</p>\r\n<p>&nbsp;天空下著大雨，空氣中彌漫著另人窒息的氣味，普拉多艱難的邁著沉重的步子，朝著釧古樓走去。&ldquo;今天應該是一個了斷了&rdquo;他對自己說。&ldquo;不能猶豫，否則又將是痛苦的開始&rdquo;。</p>','700','ANONYMOUS@TECHWISE.COM.HK',1235974836,NULL,NULL,''),(5,NULL,'AAAA','<p>AAAA</p>','700','CO@TECHWISE.COM.HK',1242659396843,NULL,1242659396843,'0'),(6,NULL,'AAAA','<p>AAAA</p>','700','CO@TECHWISE.COM.HK',1242659396843,NULL,1242659396843,'0'),(7,25,'ABC','<p>&nbsp;ABCBBB</p>','700','LIANGLIJUAN@TECHWISE.COM.HK',1270707853750,'LIANGLIJUAN@TECHWISE.COM.HK',1271407720375,'0'),(8,27,'立項報告','<p>暫無</p>','700','LIANGLIJUAN@TECHWISE.COM.HK',1270781184921,'LIANGLIJUAN@TECHWISE.COM.HK',1271384361406,'0');
